if (!global.get('systemClockSynchronized')) return null;
var mqttArray = msg.topic.split('/');
var device = 
{
    project     : mqttArray[0],
    deviceType  : mqttArray[1],
    name        : mqttArray[2],
    prop        : mqttArray[3],
};
return {topic : msg.topic, payload:{topic:'readings', mqttDevice : device, payload:msg.payload}};

return {
    topic   : msg.payload.mqttMessage.topic, 
    payload : msg.payload.mqttMessage.payload, 
    token   : msg.payload.token, 
    userID  : msg.payload.userID };

if (global.get('settingsPassword') == 'NULL')
{
    return [null, {topic:'getJwtToken',payload:msg.payload.userID, userID : msg.payload.userID}];    
}
if (msg.payload.payload.loginPassword != global.get('settingsPassword'))
{
    return [{topic:'getJwtToken', payload:{topic:'getJwtToken', userID:msg.payload.userID, token: null}}, null];
}
return [null, {topic:'getJwtToken',payload:msg.payload.userID, userID : msg.payload.userID}];

if (Number(msg.token) == msg.userID)
{
    return [msg, {topic : 'acknowledge', payload:{topic:'settingAcknowledge', payload: 'Success!', userID : msg.userID}}]
}
return [null, {topic : 'acknowledge', payload:{topic:'settingAcknowledge', payload: 'Bad token', userID : msg.userID}}];

var projectionFilter =  
{
    projection:
    {
        _id                         :   0, 
    } 
};
var queryFilter = 
{
    userID: msg.userID
}
var settingLogEntry = {topic:msg.topic, userID: msg.userID, device:msg.payload, timeStamp: new Date().getTime()};
var newMsg = 
{
    topic           : 'getAccessLog',
    payload         : [queryFilter, projectionFilter],
    settingLogEntry : settingLogEntry
};
return newMsg;

var settingLogEntry = 
{
    topic       :   msg.settingLogEntry.topic, 
    userID      :   msg.settingLogEntry.userID,
    device      :   msg.settingLogEntry.device,
    timeStamp   :   msg.settingLogEntry.timeStamp,
    ipInfo      :   msg.payload
}
return {topic: 'settingLogEntry', payload: settingLogEntry};

template:
        var ws;
        var userID = -1;
        var useWebSocketsFlag = true;
        var projectName = '{{{payload.projectname}}}';
        var heartBeatImage = false;
        var wsUri = '';
        var wsConnected = false;

        function webSocketConnected()
        {
            return wsConnected;
        }
        function useWebSockets(webSocketsFlag)
        {
            useWebSocketsFlag = webSocketsFlag;
        }
        function wsConnectC()
        {
            if (!useWebSocketsFlag)
            {
                return;
            }
            if (wsUri.length < 1)
            {
                var uri = window.location.href.split('://');
                var wslead = 'ws://';
                if (uri[0] == 'https') wslead = 'wss://';
                var questionLocation = uri[1].indexOf('?');
                if (questionLocation >= 0)
                {
                    uri[1] = uri[1].substring(0,questionLocation);
//                    console.log(uri[1]);
                }
                if (uri[1].indexOf('/') < (uri[1].length - 1))
                {
                    wsUri = wslead + uri[1] + '/websocket';
                }
                else
                {
                    wsUri = wslead + uri[1] + 'websocket';
                }
            }
            ws = new WebSocket(wsUri);
            ws.onmessage = function(event)
            {
                onWebSocketMessage(JSON.parse(event.data));

            };
            ws.onopen = function()
            {
                console.log("Websocket connected");
                wsConnected = true;
                $('#webSocketStatus').html('Open');
                $('#reOpenWebSocketButton').hide();
                $('#webSocketStatusRow').css('padding-bottom','0px');
                $('#webSocketStatusCard').hide();
                onWebSocketOpen();
            };
            ws.onclose = function()
            {
                console.log("Websocket closed");
                wsConnected = false;
                $('#webSocketStatus').html('Closed');
                $('#reOpenWebSocketButton').show();
                $('#webSocketStatusCard').show();
                $('#webSocketStatusRow').css('padding-bottom','25px');
                onWebSocketClose();
                wsConnectC();
            };
        }
        function sendWebSocketMessageObject(message)
        {
            if (!webSocketConnected())
            {
                console.log("Reconnecting WebSocket");
                wsConnectC();
                setTimeout(function(){ ws.send(JSON.stringify(message)); }, 2000);
            }
            else
            {
                ws.send(JSON.stringify(message));
            }
        }

        function getRandomInt(max)
        {
          return Math.floor(Math.random() * Math.floor(max));
        }
        $( document ).ready(function()
        {
            loginDialog("close");
//            userID  = getRandomInt(32768);
            userID  = {{{payload.userID}}};
            if (!useWebSocketsFlag)
            {
                $('#webSocketStatusRow').css('padding-bottom','0px');
                $('#webSocketStatusCard').hide();
            }
            wsConnectC();
            onDocumentReady();
            if ({{{payload.setServerTimeFlag}}})
            {
                var windowLocation = window.location.href
                if (window.location.href.indexOf('/setServerTime') < 0) 
                {
                    window.location.replace('/setServerTime?parentRef=' + windowLocation);
                }
            }
        });
        $( function() 
        {
            $( "#acknowledgeDialog" ).dialog(
                {
                    width:    500,
                    autoOpen: false,
                    buttons:
                    [
                        {
                            text: "Ok",
                            click: function() { $( this ).dialog( "close" );},
                            showText: false
                        },
                   ]
                });
            $( "#messageDialog" ).dialog(
                {
                    width:    500,
                    autoOpen: false,
                    buttons:[]
                });
        } );
        function optionDialog(headerText, title, text, buttonTexts, buttonFunctions)
        {
            var buttonDefinitions = [];
            for (var ii = 0; ii < buttonTexts.length; ++ii)
            {
                buttonDefinitions[ii] = 
                    {
                        text: buttonTexts[ii],
                        click: buttonFunctions[ii],
                        showText: false
                    };
            }
            $( "#optionDialog" ).dialog( "option", "title", headerText );   
            $( "#optionDialog" ).dialog( "option", "buttons", buttonDefinitions);
            $( "#optionDialogTitle" ).html(title);
            $( "#optionDialogText" ).html(text);
            $( "#optionDialog" ).dialog( "open" );
        }
        $( function() 
        {
            $( "#optionDialog" ).dialog(
                {
                    width:    500,
                    autoOpen: false,
                    buttons:
                    [
                        {
                            text: "Ok",
                            click: function() {$( this ).dialog( "close" );},
                            showText: false
                        },
                        {
                            text: "Cancel",
                            click: function() {$( this ).dialog( "close" );},
                            showText: false
                        },
                   ]
                });
        } );
        function acknowledgeDialog(headerText, title, text)
        {
            $( "#acknowledgeDialog" ).dialog( "option", "title", headerText );    
            $( "#acknowledgeDialogTitle" ).html(title);
            $( "#acknowledgeDialogText" ).html(text);
            $( "#acknowledgeDialog" ).dialog( "open" );
        }
        function messageDialog(headerText, title, text)
        {
            $( "#messageDialog" ).dialog( "option", "title", headerText );    
            $( "#messageDialogTitle" ).html(title);
            $( "#messageDialogText" ).html(text);
            $( "#messageDialog" ).dialog( "open" );
        }
        var jwtToken = '';
        var loginCallback = null;
        var loginCallbackInfo = null;
        function checkLogin(callback, callbackInfo)
        {
            if (jwtToken.length > 0) return callback(callbackInfo);
            loginDialog("open", callback, callbackInfo);
        }
        function loadJwtToken(data)
        {
            if (data.token == null)
            {
                acknowledgeDialog('Error', 'Login', 'Denied');
            }
            else
            {
                jwtToken = data.token;
                loginCallback(loginCallbackInfo);
            }
        }
        function loginDialog(action, callback, callbackInfo) 
        {
            $( "#loginDialog" ).dialog(
                {
//                    closeOnEscape: false,
//                    open: function(event, ui) {$(".ui-dialog-titlebar-close", ui.dialog | ui).hide();},
                    width:    500,
                    autoOpen: false,
                    buttons:
                    [
                        {
                            text: "Login",
                            click: function() 
                            {
                                var tokenMsg = 
                                {
                                    topic       : 'getJwtToken',
                                    payload     : 
                                    {
//                                        loginUser       : $('#loginUser').val(),
                                        loginPassword   : $('#loginPassword').val()
                                    },
                                    'userID'    : userID,
                                };                               
                                if (!webSocketConnected())
                                {
                                    console.log("Reconnecting WebSocket");
                                    wsConnectC();
                                    setTimeout(function(){ ws.send(JSON.stringify(tokenMsg)); }, 2000);
                                }
                                else
                                {
                                    ws.send(JSON.stringify(tokenMsg));
                                }
                                ws.send(JSON.stringify(tokenMsg));
                                loginCallback = callback;
                                loginCallbackInfo = callbackInfo;
                                $( this ).dialog( "close" );
                            },
                            showText: false
                        },
                        {
                            text: "Cancel",
                            click: function() {$( this ).dialog( "close" );},
                            showText: false
                        }
                    ]
                });
            $( "#loginDialog" ).dialog(action);
        }
        function sendSettingToMqtt(mqttMessage)
        {
            var socketMessage = 
            {
                topic       : 'sendSettingToMqtt',
                mqttMessage : mqttMessage,
                token       : jwtToken,
