        function displayDevice(index)
        {
            var actionArray = [];
            var attrList = [];
            var keys = Object.keys(settingsHistory[index].device);
            for (var ii = 0; ii < keys.length; ++ii)
            {
                if (settingsHistory[index].device[keys[ii]].hasOwnProperty('type'))
                {
                    if (settingsHistory[index].device[keys[ii]].type == 'scalar')
                    {
                        if (settingsHistory[index].device[keys[ii]].action == 'setting')
                        {
                            actionArray.push(settingsHistory[index].device[keys[ii]]);
                            attrList.push(keys[ii]);
                        }
                    }
                }
            }

            var time = new Date(settingsHistory[index].timeStamp).toLocaleString('en-US',{year: 'numeric', month: '2-digit', day : '2-digit', hour12:false, hour: '2-digit', minute: '2-digit'}).replace(',', '');

