// Archive JavaScript
        function getArchive(hours)
        { 
            $('#waitForArchiveMessage').show();
            $('#archive2hrButton').attr("disabled", true); 
            $('#archive4hrButton').attr("disabled", true); 
            $('#archive8hrButton').attr("disabled", true); 
            $('#archive24hrButton').attr("disabled", true); 
            $('#archive72hrButton').attr("disabled", true); 
            $('#archive168hrButton').attr("disabled", true); 
            $('#archive2hrButton').css('color', 'var(--color5)'); 
            $('#archive2hrButton').css('background-color', 'var(--color1)'); 
            $('#archive4hrButton').css('color', 'var(--color5)'); 
            $('#archive4hrButton').css('background-color', 'var(--color1)'); 
            $('#archive8hrButton').css('color', 'var(--color5)'); 
            $('#archive8hrButton').css('background-color', 'var(--color1)'); 
            $('#archive24hrButton').css('color', 'var(--color5)'); 
            $('#archive24hrButton').css('background-color', 'var(--color1)'); 
            $('#archive72hrButton').css('color', 'var(--color5)'); 
            $('#archive72hrButton').css('background-color', 'var(--color1)'); 
            $('#archive168hrButton').css('color', 'var(--color5)'); 
            $('#archive168hrButton').css('background-color', 'var(--color1)'); 
            switch(hours)
            {
                case 2:
                    $('#archive2hrButton').css('color', 'var(--color1)'); 
                    $('#archive2hrButton').css('background-color', 'var(--color5)'); 
                    break;
                case 4:
                    $('#archive4hrButton').css('color', 'var(--color1)'); 
                    $('#archive4hrButton').css('background-color', 'var(--color5)'); 
                    break;
                case 8:
                    $('#archive8hrButton').css('color', 'var(--color1)'); 
                    $('#archive8hrButton').css('background-color', 'var(--color5)'); 
                    break;
                case 24:
                    $('#archive24hrButton').css('color', 'var(--color1)'); 
                    $('#archive24hrButton').css('background-color', 'var(--color5)'); 
                    break;
                case 72:
                    $('#archive72hrButton').css('color', 'var(--color1)'); 
                    $('#archive72hrButton').css('background-color', 'var(--color5)'); 
                    break;
                case 168:
                    $('#archive168hrButton').css('color', 'var(--color1)'); 
                    $('#archive168hrButton').css('background-color', 'var(--color5)'); 
                    break;
                default:
                // code block
            }
            archiveRequestList = [];
            archiveList = [];
            var stopDate = new Date().getTime();
            var startDate = stopDate - hours * 3600000;
            archiveRequestList[0] = 
            {
                deviceType : restfulDevice.deviceType,
                name : restfulDevice.name, 
                attr : ['beamCurrent','avgDayCurrent','lifetime','currentRate'],
                startDate: startDate, 
                stopDate: stopDate,
            };
           getArchiveValues();
        }
        function getArchiveValues()
        {
            sendWebSocketMessageObject(
                {
                    topic       : 'getArchive',
                    userID      : userID,
                    device      : archiveRequestList[0],
                 });
            archiveRequestList.shift();
            return;
        }
        function putArchiveValues(data)
        {
            archiveList.push(data);
            if (archiveRequestList.length > 0)
            {
                getArchiveValues();
            }
            else
            {
                plotArchive();
            }
        }
