                    yaxis: 'y1',
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: this.plotConfig.trace1Color}

                };
                this.trace2 = 
                {
                    x: [],
                    y: [],
                    name: 'Int. Beam Current (mA-Day)',
                    yaxis: 'y1',
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: 'green'}

                };
                this.trace3 = 
                {
                    x: [],
                    y: [],
                    name: 'Lifetime (hrs)',
                    yaxis: 'y2',
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: this.plotConfig.trace2Color}

                };
                this.trace4 = 
                {
                    x: [],
                    y: [],
                    name: 'Accum. Rate (mA/min)',
                    yaxis: 'y2',
                    type: 'scatter',
                    mode: 'lines',
                    line: {color: 'red'}

                };
            }
            addTracePoints(trace1Pt, trace2Pt, trace3Pt,trace4Pt)
            {
                var now = (new Date().getTime() - this.plotStartTime) / 1000;
                var plotBeginning = now - this.plotConfig.plotLength;
                var removeDone = false;
                while(!removeDone)
                {
                    if (this.trace1.x[0] < plotBeginning)
                    {
                        this.trace1.x.shift();
                        this.trace1.y.shift();
                        this.trace2.x.shift();
                        this.trace2.y.shift();
                        this.trace3.x.shift();
                        this.trace3.y.shift();
                        this.trace4.x.shift();
                        this.trace4.y.shift();
                    }
                    else
                    {
                        removeDone = true;
                    }
                }

                this.trace1.x.push(now);
                this.trace1.y.push(trace1Pt);
                this.trace2.x.push(now);
                this.trace2.y.push(trace2Pt);
                this.trace3.x.push(now);
                this.trace3.y.push(trace3Pt);
                this.trace4.x.push(now);
                this.trace4.y.push(trace4Pt);
                Plotly.newPlot(this.plotConfig.htmlId, [this.trace1, this.trace2, this.trace3, this.trace4], this.layout);
            }
        }

