if (!msg.payload.hasOwnProperty('deviceType'))
{
    msg.payload = 'deviceType not defined\n';
    return [null,msg];
}
if (!msg.payload.hasOwnProperty('name'))
{
    msg.payload = 'name not defined\n';
    return [null,msg];
}
if (!msg.payload.hasOwnProperty('attr'))
{
    msg.payload = 'attr not defined\n';
    return [null,msg];
}
if (!msg.payload.hasOwnProperty('value'))
{
    msg.payload = 'value not defined\n';
    return [null,msg];
}
if (!msg.payload.hasOwnProperty('password'))
{
    msg.payload = 'password not defined\n';
    return [null,msg];
}
if (msg.payload.password != global.get('settingsPassword'))
{
    msg.payload = 'wrong password\n';
    return [null,msg];
}
var queryMsg = 
[
    {
        $and    :
        [
            {
                deviceType  :   msg.payload.deviceType
            },
            {
                name        :   msg.payload.name
            }
        ]
    },
    {
        projection: 
        {
            _id :   0
        }
    }
];
var inputData = JSON.parse(JSON.stringify(msg.payload));
msg.payload = queryMsg;
msg['inputData'] = inputData;
return [msg,null];

var device = null;
if (isEmpty(msg.payload))
{
    msg.payload = 'device not found\n';
    return [msg,null];
}
else
{
    device = JSON.parse(JSON.stringify(msg.payload));
}
if (!device.hasOwnProperty(msg.inputData.attr))
{
    msg.payload = 'attr not found\n';
    return [msg,null];
}
if (device[msg.inputData.attr].action != 'setting')
{
    msg.payload = 'Not a setting\n';
    return [msg,null];
}
var settingValue = msg.inputData.value;
if (device[msg.inputData.attr].type == 'scalar') settingValue = Number(settingValue);
device[msg.inputData.attr].value = settingValue;
msg.payload = 'Success!\n';
var topic = global.get('projectName') + '/' + device.deviceType + '/' + device.name + '/setting';
return [msg,{topic:topic,payload:device}];
function isEmpty(obj) 
{
    for(var prop in obj) 
    {
        if(obj.hasOwnProperty(prop))
            return false;
    }

    return true;
}

