var restfulDevice = 
{
    deviceType  : '{{{payload.script.deviceType}}}',
    name        : '{{{payload.script.name}}}',
    project     : '{{{payload.script.project}}}'
};
var mqttDevice = null;
var device = null;
var plotStart = new Date();

useWebSockets(true);
function onDocumentReady()
{
}
function onWebSocketOpen()
{
    acknowledgeDialog('Alert', 'Wait!', 'Contacting the Mother Ship...');
}
function onWebSocketClose()
{

}
function onWebSocketMessage(msg)
{
    switch(msg.topic)
    {
        case 'readings':
            if (msg.mqttDevice.name == restfulDevice.name)
            {
                heartbeat();
                device = msg.payload;
                mqttDevice = msg.mqttDevice;
                updateDmaReadings();
            }
            break;
        case 'getJwtToken':
            if (msg.userID == userID) loadJwtToken(msg);
            break;
        case 'settingAcknowledge':
            if (msg.userID == userID)
            {
                if (msg.payload != 'Success!') acknowledgeDialog('Acknowledge', 'Setting', msg.payload);
            }
            break;
        default:
        // code block
    }
}

function updateDmaReadings()
{
    phasePlot();
    modePlot();
    bunchPlot();
    dataGlobal();
    $( "#acknowledgeDialog" ).dialog('close');
}
function dataGlobal()
{
    $("#beamCurrent").text(Math.round(device.beamCurrent.value * 100.0) / 100.0);
}
function phasePlot()
{
    var normalCardStyle = '#575491';
    var normalCardTitleStyle = '#fdc300';
    var normalCardBodyStyle = '#9f9dc7';
    var normalTraceColor = '#575491';
    var alarmCardStyle = 'red';
    var alarmCardTitleStyle = 'white';
    var alarmCardBodyStyle = 'pink';
    var alarmTraceColor = 'red';

    var cardStyle = normalCardStyle;
    var cardTitleStyle = normalCardTitleStyle;
    var cardBodyStyle = normalCardBodyStyle;
    var traceColor = normalTraceColor;
    if (device.beamCurrent.value > 50.0)
    {
        if (device.maxDipoleModeAmp.value > device.maxDipoleModeAmp.alarm.limits.high)
        {
            cardStyle = alarmCardStyle;
            cardTitleStyle = alarmCardTitleStyle;
            cardBodyStyle = alarmCardBodyStyle;
            traceColor = alarmTraceColor;
        }
    }
    $('#phaseCard').css('background-color',cardStyle);
    $('#phaseCardBody').css('background-color',cardBodyStyle);
    $('#phaseCardTitle').css('color',cardTitleStyle);
    var ymax = device.maxDipoleModeAmp.alarm.limits.hihi;
    var layout =
    {   
        title: 'Phase',
        showlegend: false,
        xaxis: {title: 'Bunch Number'},
        yaxis: {title: 'phase (pS)', range: [-ymax,ymax]}
    };
    var traceData = [{}];
    var phase = device.difPhase.value;
    traceData[0] = {x: [],y: [],name: 'phase', yaxis: 'y1',type: 'bar',marker: {color: traceColor}};
    for (var ii = 0; ii < phase[0].length; ++ii)
    {
        traceData[0].x[ii] = phase[0][ii];
        traceData[0].y[ii] = phase[1][ii];
    }
    Plotly.newPlot('phasePlot', traceData, layout);
}
function modePlot()
{
    var normalCardStyle = '#575491';
    var normalCardTitleStyle = '#fdc300';
    var normalCardBodyStyle = '#9f9dc7';
    var normalTraceColor = '#575491';
    var alarmCardStyle = 'red';
    var alarmCardTitleStyle = 'white';
    var alarmCardBodyStyle = 'pink';
    var alarmTraceColor = 'red';

    var cardStyle = normalCardStyle;
    var cardTitleStyle = normalCardTitleStyle;
    var cardBodyStyle = normalCardBodyStyle;
    var traceColor = normalTraceColor;
    if (device.beamCurrent.value > 50.0)
    {
        if (device.maxDipoleModeAmp.value > device.maxDipoleModeAmp.alarm.limits.high)
        {
            cardStyle = alarmCardStyle;
            cardTitleStyle = alarmCardTitleStyle;
            cardBodyStyle = alarmCardBodyStyle;
            traceColor = alarmTraceColor;
        }
    }
    $('#modeSpectrumCard').css('background-color',cardStyle);
    $('#modeSpectrumCardBody').css('background-color',cardBodyStyle);
    $('#modeSpectrumCardTitle').css('color',cardTitleStyle);
    var ymax = device.maxDipoleModeAmp.alarm.limits.high;
    var yscale = Math.floor(Math.log10(ymax)) + 1;
    var layout =
    {   
        title: 'Mode Spectrum',
        xaxis: {title: 'Mode Number'},
        yaxis: {title: 'Amplitude (pS)', type: 'log', range: [0,yscale]},
        shapes: [{
                    type: 'rect',
                    xref: 'paper',
                    x0: 0,
                    x1: 1,
                    yref: 'y',
                    y0: ymax,
                    y1: ymax + 1,
                    line: { width: 0},
                    fillcolor: 'red'
                }]
    };
    var modeM = device.modeM.value;
    var traceData = [{}];
    traceData[0] = {x: [],y: [],name: 'mode', yaxis: 'y1',type: 'bar',marker: {color: traceColor}};
    for (var ii = 0; ii < modeM[0].length; ++ii)
    {
        traceData[0].x[ii] = modeM[0][ii];
        traceData[0].y[ii] = modeM[1][ii];
    }
    Plotly.newPlot('modePlot', traceData, layout);
}
function bunchPlot()
{
    var layout =
    {   
        title: 'Bunch Intensity',
        showlegend: false,
        xaxis: {title: 'Bunch Number'},
        yaxis: {title: 'Bunch Current (mA)'},
    };
    var bunchCurrent = device.bunchCurrent.value;
    var traceData = [{}];
    traceData[0] = {x: [],y: [],name: 'bunch Intensity', yaxis: 'y1',type: 'bar',marker: {color: '#575491'}};
    for (var ii = 0; ii < bunchCurrent[0].length; ++ii)
    {
        traceData[0].x[ii] = bunchCurrent[0][ii];
        traceData[0].y[ii] = bunchCurrent[1][ii];
    }
    Plotly.newPlot('bunchPlot', traceData, layout);
}

