function loadDeviceList(deviceList)
{
    deviceList.sort();
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        for (var ii = 0; ii < deviceList.length; ++ii)
        {
            var opt = document.createElement('option');
            opt.value = deviceList[ii];
            opt.innerHTML = deviceList[ii];
            deviceSelect[itrace].appendChild(opt);
        }
    }
}
function loadNameList(data)
{
    data.nameList.sort();
    for (var ii = 0; ii < data.nameList.length; ++ii)
    {
        var opt = document.createElement('option');
        opt.value = data.nameList[ii];
        opt.innerHTML = data.nameList[ii];
        nameSelect[data.trace].appendChild(opt);
    }

    return;
}
function loadAttrList(data)
{
    data.attrList.sort();
    for (var ii = 0; ii < data.attrList.length; ++ii)
    {
        var opt = document.createElement('option');
        opt.value = data.attrList[ii];
        opt.innerHTML = data.attrList[ii];
        attrSelect[data.trace].appendChild(opt);
    }
}
function deviceSelected(itrace)
{
    clearNameOptionList(itrace);
    if (deviceSelect[itrace].value == 'notSelected') return;
    nameSelect[itrace].value = 'notSelected';
    sendWebSocketMessageObject(
        {
            topic       : 'getNameList',
            userID      : userID,
            trace       : itrace,
            deviceType  : deviceSelect[itrace].value,
        });
}
function nameSelected(itrace)
{
    clearAttrOptionList(itrace);
    if (nameSelect[itrace].value == 'notSelected') return;
    sendWebSocketMessageObject(
        {
            topic       : 'getAttrList',
            userID      : userID,
            trace       : itrace,
            deviceType  : deviceSelect[itrace].value,
            name        : nameSelect[itrace].value,
            prop        : 'reading'
        });
}
function attrSelected(itrace)
{
    devInfo[itrace].deviceType = '';
    devInfo[itrace].name = '';
    devInfo[itrace].attr = '';
    devInfo[itrace].device = {};
    if (attrSelect[itrace].value == 'notSelected')  return;
    devInfo[itrace].deviceType = deviceSelect[itrace].value;
    devInfo[itrace].name = nameSelect[itrace].value;
    devInfo[itrace].attr = attrSelect[itrace].value;

}
function clearNameOptionList(itrace)
{
    devInfo[itrace].deviceType = '';
    devInfo[itrace].name = '';
    devInfo[itrace].attr = '';
    devInfo[itrace].device = {};

    while (nameSelect[itrace].firstChild)
    {
        nameSelect[itrace].removeChild(nameSelect[itrace].firstChild);
    }
    var opt = document.createElement('option');
    opt.value = 'notSelected';
    opt.innerHTML = '';
    nameSelect[itrace].appendChild(opt);
    clearAttrOptionList(itrace);
}
function clearAttrOptionList(itrace)
{
    devInfo[itrace].deviceType = '';
    devInfo[itrace].name = '';
    devInfo[itrace].attr = '';
    devInfo[itrace].device = {};

    while (attrSelect[itrace].firstChild)
    {
        attrSelect[itrace].removeChild(attrSelect[itrace].firstChild);
    }
    var opt = document.createElement('option');
    opt.value = 'notSelected';
    opt.innerHTML = '';
    attrSelect[itrace].appendChild(opt);

}
function getArchiveData()
{

    archiveRequestList = [];
    archiveList = [];
    startDate = new Date($( "#startDate" ).val()).getTime();
    stopDate = new Date($( "#stopDate" ).val()).getTime();
    var traceCount = 0;
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        if (devInfo[itrace].attr.length > 0) 
        {
            archiveRequestList[traceCount] = 
            {
                deviceType  : devInfo[itrace].deviceType, 
                name        : devInfo[itrace].name, 
                prop        : devInfo[itrace].prop, 
                attr        : devInfo[itrace].attr,
                startDate: startDate, 
                stopDate: stopDate,
                itrace  :   itrace
            };
            ++traceCount;
        }
    }
    if (traceCount < 1) return;
    $('#plotSetupTable').hide();
    $('#timePlotCard').hide();
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        $('#csv'+ itrace.toString()).removeAttr("href");
        $('#csv'+ itrace.toString()).css('color', 'black');
        $('#csv'+ itrace.toString()).css('text-decoration', 'none');
        $('#csv'+ itrace.toString()).css('font-weight', 'bold');
    }
    messageDialog('Alert', 'Wait!', 'Requesting data ...');
    getArchiveValues();
}
function getArchiveValues()
{
    var maxPtsToPlot = Number($( "#maxPtsToPlot" ).val());
    sendWebSocketMessageObject(
        {
            topic       : 'getArchiveValues',
            userID      : userID,
            device      : archiveRequestList[0],
            maxPtsToPlot: maxPtsToPlot
        });
    archiveRequestList.shift();
    return;
}
function plotArchive()
{
    $( "#messageDialog" ).dialog('close');
    $('#plotSetupTable').show();

    $('#plotViewButtons').show();
    $('#timePlot').show();
    makeCsvFile();
    var npts = archiveList[0].trace.length;
    var oodataArray = [];

    var nxMax = archiveList[0].trace[0].value[0].length;
    for (var ii = 0; ii < npts; ++ii)
    {
        var yvalue = Math.round((archiveList[0].trace[ii].timeStamp - archiveList[0].trace[0].timeStamp)/3600) / 1000;
        for (var ix = 0; ix < nxMax; ++ix)
        {
            var zvalue = archiveList[0].trace[ii].value[1][ix];
            oodataArray[ii * nxMax + ix] =
            {
                'x': archiveList[0].trace[ii].value[0][ix],
                'y': yvalue,
                'z': zvalue,
                'style': zvalue
            };
        }
    }
    var options = 
    {
        width:  '100%',
        height: '800px',
        style: 'surface',
        showPerspective: false,
        showGrid: true,
        showShadow: false,
        keepAspectRatio: false,
        verticalRatio: 1.0,
        showZAxis: true,
        yCenter: '50%',
        xLabel: 'x',
        yLabel: 'Time (hr)',
        zLabel: 'y',
        tooltip: true,
        axisColor: '#000000'
    };

    // create a graph3d
    var container = document.getElementById('timePlot');

    graph3d = new vis.Graph3d(container, oodataArray, options);
    var horzAngle = 0 * 3.1415927 / 180.0;
    var vertAngle = 90 * 3.1415927 / 180.0;
    graph3d.setCameraPosition({'horizontal': horzAngle, 'vertical': vertAngle, 'distance': 2.0});
}
function changePlotView(horzAngleDeg, vertAngleDeg)
{
    var horzAngle = horzAngleDeg * 3.1415927 / 180.0;
    var vertAngle = vertAngleDeg * 3.1415927 / 180.0;
    graph3d.setCameraPosition({'horizontal': horzAngle, 'vertical': vertAngle, 'distance': 2.0});
}
function makeCsvFile ()
{
    var itrace = 0;
    var dataString = '';
    var startT = archiveList[0].trace[0].timeStamp;

    dataString = dataString + 'Device,' + devInfo[0].deviceType + ',' + devInfo[0].name + ',' + devInfo[0].prop + ',' + devInfo[0].attr  +'\n';
    dataString = dataString + 'StartDate,' + new Date(startT).toLocaleString('en-US',{year: 'numeric', month: '2-digit', day : '2-digit', hour12:false, hour: '2-digit', minute: '2-digit'}) + '\n';
    dataString = dataString + 'StartDate (mS),' + startT.toString() + '\n';
    dataString = dataString + 'Time (sec),  \n 0,x';
    for (var ipt = 0; ipt < archiveList[0].trace[0].value[0].length; ++ipt) dataString = dataString + ',' + archiveList[0].trace[0].value[0][ipt];
    dataString = dataString + '\n';

    for (var ii = 0; ii < archiveList[0].trace.length; ++ii)
    {

        var x = (archiveList[0].trace[ii].timeStamp - startT) / 1000;
        dataString = dataString + x.toString() + ',y';
        for (var ipt = 0; ipt < archiveList[0].trace[ii].value[1].length; ++ipt) dataString = dataString + ',' + archiveList[0].trace[ii].value[1][ipt];
        dataString = dataString + '\n';
    }
    var data = new Blob([dataString], {type: 'text/plain'});

    // If we are replacing a previously generated file we need to
    // manually revoke the object URL to avoid memory leaks.
    if (csvFile[itrace] !== null) {
      window.URL.revokeObjectURL(csvFile[itrace]);
    }

    csvFile[itrace] = window.URL.createObjectURL(data);
    // returns a URL you can use as a href
    $('#csv'+ itrace.toString()).css('color', 'blue');
    $('#csv'+ itrace.toString()).css('text-decoration', 'underline');
    $('#csv'+ itrace.toString()).css('font-weight', 'bold');
    $('#csv'+ itrace.toString()).attr("href", csvFile[itrace]);
    $('#csv'+ itrace.toString()).attr("download", devInfo[itrace].deviceType + '-' + devInfo[itrace].name + '-' + devInfo[itrace].prop + '-' + devInfo[itrace].attr + '.csv');

}

