function objectLength( object ) {
    var length = 0;
    for( var key in object ) {
        if( object.hasOwnProperty(key) ) {
            ++length;
        }
    }
    return length;
}
var numNames = objectLength(msg.payload);
var nameList = [];

for (var iname = 0; iname < numNames; ++iname)
{
    nameList[iname] = msg.payload[iname].name;
}
return {
    topic:'getNameList',
    payload:{
        topic           : 'getNameList',
        nameList        : nameList,
        userID          : msg.userID,
        deviceType      : msg.deviceType,
        trace           : msg.trace,
    }
};

